describe('functionality testing suit', function(){
	it('should Identify the address', function(){
		browser.get('http://localhost:3000/');
		expect(browser.getCurrentUrl()).toContain('localhost:3000');
	})
	it('should click on the first link', function(){
		var link = element(by.css('[data-ng-click="main.select(1)"]'));
		expect(link.getText()).toEqual('I am having technical problems with the game');
	})
	it('should click on the first link', function(){
		var link = element(by.css('[data-ng-click="main.select(2)"]'));
		expect(link.getText()).toEqual('I have a suggestion to make the game better!');
	})
	it('should click on the first link', function(){
		var link = element(by.css('[data-ng-click="main.select(3)"]'));
		expect(link.getText()).toEqual("I'd like to request a refund for a purchased item");
	})
})
		
		
		//element(by.model('tech.problem')).sendKeys('no sound');
